mapper.select:
select 

from UPAY_CSYS_IMSI_LD_CD
where IMSI_AREA_ID = #{imsiAreaId,jdbcType=VARCHAR}
  and EFFC_TM = #{effcTm,jdbcType=VARCHAR}
     and (getdate() >= convert(datetime, effc_tm) and getdate() <= convert(datetime, expired_tm))

mapper.delete:
delete from UPAY_CSYS_IMSI_LD_CD
where IMSI_AREA_ID = #{imsiAreaId,jdbcType=VARCHAR}
  and EFFC_TM = #{effcTm,jdbcType=VARCHAR}

	 delete from UPAY_CSYS_IMSI_LD_CD
  where IMSI_AREA_ID = #{imsiAreaId,jdbcType=VARCHAR}
    and EFFC_TM = #{effcTm,jdbcType=VARCHAR}
