mapper.select:
 select

from UPAY_CSYS_TMALL_BILL_PAY
where 1=1

  and int_txn_seq = #{params.intTxnSeq}


  and int_trans_code = #{params.intTransCode}


  and int_txn_date = #{params.intTxnDate}


  and settle_date = #{params.settleDate}


and tmall_trans_id = #{params.tmallTransId}


and tmall_trans_dt = #{params.tmallTransDt,jdbcType=CHAR}


and  tmall_rsp_code = #{params.tmallRspCode,jdbcType=VARCHAR}


and  ori_opr_trans_id = #{params.oriOprTransId,jdbcType=VARCHAR}


and  tmall_route_info = #{params.tmallRouteInfo,jdbcType=VARCHAR}


and  tmall_org_id = #{params.tmallOrgId,jdbcType=VARCHAR}


and  status = #{params.status,jdbcType=VARCHAR}




          order by ${params.orderParam}


          order by last_upd_time desc

